package misc;

public class even_odd_num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0; i<=10; i++) {
		    if(i % 2==1 ){
		        System.out.println(i);
		    }
		}

		for(int i=0; i<=10; i++) {
		    if(i % 2==0) {
		        System.out.println(i);
		    }
		}

	}

}
